$(document).ready(function(){document.onkeydown=function(b){if(b.ctrlKey||b.metaKey||b.altKey){return}else{if(b.keyCode===13){b.preventDefault();a()}}};function a(){var c=$("#username").val();var b=$("#password").val();if(c==""||b==""){if(c==""){$("#errormsg").html("<center><p class='text-error'>* Please input username. *</p></center>")}else{if(b==""){$("#errormsg").html("<center><p class='text-error'>* Please input password. *</p></center>")}}return}$.post("login_check.php",{user:c,password:b,submit:1},function(e,d){if(e=="success"){window.location.assign("main.php")}else{$("#errormsg").html("<center><p class='text-error'>* Incorrect username or password. *</p></center>")}})}$("#signin").click(function(b){a()})});